import { Block } from '@ali/4ever-cangjie';
export interface HeadingGroupConfig {
    /**
     * 支持分组的标题的级别
     * @default 3
     *
     * @example
     *
     * `1`: 仅分组 h1
     *
     * `2`: 分组 h1/h2
     *
     * `3`: 分组 h1/h2/h3
     *
     * `0`: 不分组
     */
    level?: number;
    /**
     * 自动展开折叠标题
     * @example
     * - 光标进入折叠区域
     */
    autoUnfold?: boolean;
    /**
     * 忽略节点
     */
    ignore?: (node: Block) => boolean;
    /**
     * 跳过节点
     */
    pass?: (node: Block) => boolean;
}
export interface ToggleFoldPayload {
    node: Block;
    fold: boolean;
}
