export interface IHoverToolbarLayout {
    normal: string[][];
    folded?: string[][];
    splitFolded?: boolean;
}
export interface IHoverToolbarPluginConfigs {
    locale: ILocale;
    layouts?: Record<string, IHoverToolbarLayout>;
    /**
     * 是否禁用 hover toolbar
     */
    isDisabled?: () => boolean;
}
export interface ILocale {
    /**
     * 默认：「更多」
     */
    more?: string;
    /**
     * 复制
     */
    copy?: string;
    /**
     * 剪切
     */
    cut?: string;
    /**
     * 删除
     */
    delete?: string;
}
