import * as React from 'react';
interface IHrControlProps {
    onHrChange: any;
    isSelected?: boolean;
    length?: number;
    color?: string;
    lineStyle?: string;
    editor?: any;
    node?: any;
    attributes: any;
}
interface IHrControlState {
    length: number;
    originX1: number;
    originX2: number;
    offsetX: number;
    flag: string;
}
/**
 * top: 自身高度除去border / 2
 * left: 自身宽度 / 2
 */
export default class HrControl extends React.Component<IHrControlProps, IHrControlState> {
    constructor(props: any);
    /**
     * 移动算法, 计算startPoint 和 length, 即可获得具体位置
     * TODO 需要加入边界范围
     */
    handleMouseMove: (e: any) => void;
    /**
     * 回掉设置值
     */
    handleMouseUp: (e: any) => void;
    handleMouseDown: (e: any, flag: any) => void;
    render(): JSX.Element;
}
export {};
