import { Controller } from '@ali/4ever-cangjie';
import React from 'react';
import { BiPluginConfig } from '../../../types';
/**
 * 因为在中文输入法下存在keydown与cangjieInput时序不可保证
 * 因此使用onKeyUp来收拢触发移除下拉菜单的逻辑
 * 下拉菜单目前触发逻辑：
 * 1.输入/触发
 * 2.在行首输入、触发
 */
export default function createOnKeyUp(config: BiPluginConfig): (event: React.KeyboardEvent, controller: Controller, next: Function) => any;
