import { Document } from '@ali/4ever-cangjie';
/**
 * 获取默认字号
 * 当 leaf 的 mark 中获取不到字号信息时, 根据 block 的类型, 获取正文或者标题默认的字号
 * @param document
 * @param key
 * @param defaultSz 配置的默认字号
 * @returns number
 */
export default function getDefaultSz(document: Document, key?: string, defaultSz?: number): number;
