interface UseControllerOptions {
    /** 最大延迟渲染周期，指定这个参数用来控制消费了 `useController` hook 的组件，跟随编辑器内容渲染的频度 */
    maxPending?: number;
}
/**
 * 获得 Controller 实例，如果组件严格期望在编辑器内容变更后，不进行重渲，仅消费 Controller 实例，请使用 `useControllerStatic`
 * @param options
 * @returns
 */
export default function useController(options?: UseControllerOptions): import("./index").default;
export {};
