export interface VirtualizeConfig {
    /** 是否开启虚拟化 */
    enable: boolean;
    /** 是否开启视图模型的按需计算（默认开启） */
    enableOnDemandViewCalculation?: boolean;
    /** 闲时补算的 debounce 周期（默认值为 500ms） */
    invisibleCalculationDebounceTime?: number;
    /** 输入/选区 Pending 的 debounce 周期（默认值为 500ms），为 0 代表不开启 Pending */
    pendingDebounceTime?: number;
    /** 分析热区时的 OP 数目上限 */
    maxOPsForHotsAnalyze?: number;
}
