/**
 * 懒加载组件
 *
 * @param loader.fn 加载组件的函数。返回的结果会传给 render
 * @param loader.fallback 懒加载时的 placeholder。
 * @param render 渲染函数
 * @returns
 */
export declare function LazyRenderer<T>({ render, loader }: {
    loader: {
        fn: () => Promise<T>;
        fallback?: (props: {
            error?: Error;
            retry: () => void;
        }) => JSX.Element;
    };
    render: (result?: T) => JSX.Element | null;
}): JSX.Element | null;
