/// <reference types="react" />
import { Controller } from '@ali/4ever-cangjie';
/**
 * 更新 dragFragment，此处的内容应是排版前的原始数据
 * 使用 action 来更新，以便排版模式下劫持后在 behaviorController 中 getDragNodes
 */
export default function updateDragFragment(controller: Controller, payload?: {
    event: React.DragEvent;
    key: string;
}): Controller;
