export declare type PluginFactory<T, R> = (config: T) => R;
/**
 * 合并两个 PluginFactory
 * @param factory1
 * @param factory2
 */
export declare function combinePluginFactory<C1, R1, C2, R2>(factory1: PluginFactory<C1, R1>, factory2: PluginFactory<C2, R2>): PluginFactory<C1 & C2, R1 & R2>;
/**
 * 合并两组 plugins，两组 plugins 交集的部分会合并，非交集的部分将独立存在
 * @param plugins1
 * @param plugins2
 */
export declare function combinePluginsFactory<C1, R1, C2, R2>(plugins1: Record<string, PluginFactory<C1, R1>>, plugins2: Record<string, PluginFactory<C2, R2>>): Record<string, PluginFactory<C1 & C2, R1 & R2>>;
