/**
  * 通用字体族（去掉部分不用的）
  * https://developer.mozilla.org/en-US/docs/Web/CSS/font-family#%3Cgeneric-name%3E
*/
declare type GenericName = 'serif' | 'sans-serif' | 'cursive' | 'fangsong' | 'fantasy' | 'monospace' | 'system-ui' | 'emoji' | 'math';
/**
 * 字重
 * https://developer.mozilla.org/en-US/docs/Web/CSS/font-weight#Common_weight_name_mapping
 * ```
 * 100 | 200 | 300 | 400 | 500 | 600 | 700 | 800 | 900 | 950
 * ```
 */
declare type BaseFontWeight = 'Thin' | 'ExtraLight' | 'Light' | 'Normal' | 'Medium' | 'Semibold' | 'Bold' | 'ExtraBold' | 'Black' | 'ExtraBlack';
declare type FontWeight = BaseFontWeight | 'Ultralight' | 'UltraBold' | 'Regular' | 'W3' | 'W6' | 'Heavy';
