/**
 * 定义 auto 的 spacing（用于普通段落和标题）
 * defaultBeforeAutospacing/defaultAfterAutospacing 用于还原 word 中的自动段间距
 * 相比 word（上 5pt，下 10pt）会稍微紧凑一点
 * defaultBeforeSpacin/defaultAfterSpacing 为钉钉文档定义的宽松间距
 */
declare const spacing: {
    defaultBeforeAutospacing: number;
    defaultAfterAutospacing: number;
    defaultBeforeSpacing: number;
    defaultAfterSpacing: number;
    defaultCompactSpacing: number;
    defaultLinePitch: number;
    defaultLine: number;
};
/**
 * 非普通段落的默认间距（例如 代码块、card 等）
 */
export declare const defaultSpacing: {
    spacing: {
        before: number;
        after: number;
    };
};
export declare const defaultCompactSpacing: {
    spacing: {
        before: number;
        after: number;
    };
};
export default spacing;
