import { Data } from '@ali/4ever-cangjie';
export interface ShdData extends Data {
    val?: string;
    color?: string;
    fill?: string;
}
export interface UnderlineData extends Data {
    value: string;
    color?: string;
}
export interface IndentProperties {
    /**
     * 左缩进
     */
    left?: number;
    /**
     * Note: The 2006 version of the OOXML standard specified left but the 2011 version specifies start. Microsoft Word 2007 does not seem to recognize start.
     * @see http://officeopenxml.com/WPindentation.php
     */
    start?: number;
    /**
     * 左缩进（字符）
     */
    leftChars?: number;
    /**
     * 右缩进
     */
    right?: number;
    /**
     * Note: The 2006 version of the OOXML standard specified right but the 2011 version specifies end. Microsoft Word 2007 does not recognize end.
     * @see http://officeopenxml.com/WPindentation.php
     */
    end?: number;
    /**
     * 右缩进（字符）
     */
    rightChars?: number;
    /**
     * 悬挂
     */
    hanging?: number;
    /**
     * 悬挂（字符）
     */
    hangingChars?: number;
    /**
     * 首行缩进
     */
    firstLine?: number;
    /**
     * 首行缩进（字符）
     */
    firstLineChars?: number;
}
export interface SpacingProperties {
    /**
     * 文本行高
     */
    line?: number;
    /**
     * 段落前间隔：绝对值单位
     */
    before?: number;
    /**
     * 段落前间隔：行单位
     */
    beforeLines?: number;
    /**
     * 自动段落前间隔
     */
    beforeAutospacing?: boolean;
    /**
     * 段落后间隔：绝对值单位
     */
    after?: number;
    /**
     * 段落后间隔：行单位
     */
    afterLines?: number;
    /**
     * 自动段落后间隔
     */
    afterAutospacing?: boolean;
    /**
     * 行高规则
     */
    lineRule?: 'atLeast' | 'auto' | 'exact';
}
export interface ListStyleProperties {
    /**
     * 符号format
     */
    format: string;
    /**
     * 符号标示
     */
    text: string;
    /**
     * 对齐方式
     */
    align: string;
}
/**
* 列表属性
* @module Paragraph
*/
export interface ListProperties {
    /**
     * 列表 Id
     */
    listId?: string;
    /**
     * 列表缩进级别
     */
    level?: number;
    /**
     * 是否是有序列表
     */
    isOrdered?: boolean;
    /**
     * 是否是可勾选的待办列表项
     */
    isTaskList?: boolean;
    /**
     * 是否已勾选
     */
    isChecked?: boolean;
    listStyle?: ListStyleProperties;
    listStyleType?: string;
}
/**
 * tab样式
 * http://officeopenxml.com/WPtab.php
 */
export interface TabStyle {
    /**
     * 填充内容
     */
    leader?: 'dot' | 'heavy' | 'hyphen' | 'middleDot' | 'none' | 'underscore';
    /**
     * 对齐方式
     * left right为word中的老数据，新数据为start end
     */
    val?: 'bar' | 'center' | 'decimal' | 'end' | 'num' | 'start' | 'left' | 'right';
    /**
     * 位置
     */
    pos?: number;
}
