export interface SampleOptions {
    /**
     * 采样除数，采样率为 1/sample
     */
    sample: number;
}
/**
 * 装饰函数对齐进行采样
 * @param fn 采样函数
 * @param options 采样配置
 */
export declare function sample<T extends Function>(fn: T, options: SampleOptions): T;
/**
 * 函数采样装饰器
 * @param options
 */
export declare function Sample(options: SampleOptions): MethodDecorator;
