export var transMulitiToSingle = function transMulitiToSingle(text) {
  if (!text) return '';
  var quoteArr = text.split('>>>');

  for (var i = 0; i < quoteArr.length; i++) {
    if (i % 2 === 1) {
      quoteArr[i] = quoteArr[i].split('\n').join('\n> ');
    }
  }

  return quoteArr.join('');
};
export var deleteSingleEnter = function deleteSingleEnter(text) {
  var textArr = text.split('\n');

  if (textArr.length === 1) {
    return text;
  }

  var resArr = [];

  for (var i = 0; i < textArr.length; i++) {
    if (textArr[i] !== '' && textArr[i + 1] !== '' && textArr[i + 1] !== undefined) {
      textArr[i + 1] = textArr[i] + ' ' + textArr[i + 1];
    } else {
      resArr.push(textArr[i]);
    }
  }

  return resArr.join('\n');
};
export var transTabToBlock = function transTabToBlock(text) {
  text = text.replace("\s\s\s\s", '\t'); //连续四个空格为tab

  var tabArr = text.split('\t');
  tabArr = tabArr.map(function (item, index) {
    if (index > 0) {
      var tempArr = item.split('\n');
      tempArr[0] += '```';
      item = tempArr.join('\n');
    }

    return item;
  });
  console.log(tabArr.join('```'));
  return tabArr.join('```');
};