import type { IFullRegion } from '../interface';
/**
 * 获取 node 上的 align 对齐点 相对于页面的坐标
 * 比如说要得到target元素的对齐点位置,对齐在 target 的[br]
 * 首先传入的参数{left,top} 是 target的左上角位置
 * br 对应bottom-right即右下角，那么
 * right = left + width
 * bottom = top + height
 * 如果有 center , 则width/height要除于 2
 */
declare function getAlignOffset(region: IFullRegion, align: string): {
    left: number;
    top: number;
};
export default getAlignOffset;
