/// <reference types="react" />
export interface IAbsolutePopupProps {
    offsetX?: number;
    offsetY?: number;
    zIndex?: number;
    visible: boolean;
    left: number;
    top: number;
    overlay: React.ReactElement;
    overlayStyle?: React.CSSProperties;
    onVisibleChange?: (e: boolean) => void;
    /**
     * 暴露 data-testid, 用于测试
     */
    testid?: string;
}
export interface IAbsolutePopupInstance<P extends IAbsolutePopupProps> {
    update: (e: Pick<P, keyof P>) => void;
    hide: () => void;
    destory: () => void;
}
