var RECENT_COLOR_KEY = 'alidocs:we-ui:recent-colors';
export var colorUtil = {
  getRecentColorsFromLocalStorage: function getRecentColorsFromLocalStorage() {
    try {
      var colorsString = window.localStorage.getItem(RECENT_COLOR_KEY) || '[]';
      return JSON.parse(colorsString);
    } catch (e) {
      return [];
    }
  },
  saveRecentColorsToLocalStorage: function saveRecentColorsToLocalStorage(colors) {
    try {
      var colorsString = JSON.stringify(colors);
      window.localStorage.setItem(RECENT_COLOR_KEY, colorsString);
    } catch (e) {// do nothing
    }
  }
};