/// <reference types="react" />
export interface InlinePlaceholderProps {
    /**
     * 样式类型：普通 & 导出
     */
    type?: 'export' | 'normal' | 'dropdown';
    /**
     * 自定义样式
     */
    style?: React.CSSProperties;
    /**
     * 自定义类名
     */
    className?: string;
    /**
     * 图标
     */
    icon?: React.ReactNode;
    /**
     * 弹层（overlay）受控显示
     */
    visible?: boolean;
    /**
     * 是否选中
     */
    isSelected?: boolean;
    /**
     * 弹层内容
     */
    overlay?: React.ReactElement;
    /**
     * 弹层样式
     */
    overlayStyle?: React.CSSProperties;
    /**
     * 弹层偏移 X
     */
    offsetX?: number;
    /**
     * 弹层偏移 Y
     */
    offsetY?: number;
    /**
     * 弹层显示变化回调
     */
    onVisibleChange?: (e: boolean) => void;
    /**
     * 弹层自定义类名
     */
    overlayClassName?: string;
    /**
     * 点击事件
     */
    onClick?: (e: React.MouseEvent) => void;
    /**
     * 挂载元素
     */
    getContainer?: () => HTMLElement;
    /**
     * 暴露 data-testid, 用于测试
     */
    testid?: string;
    /**
     * 行为角色
     */
    role?: string;
    /**
     * 支持禁用
     */
    disabled?: boolean;
}
