import { useMemo } from 'react';
import { useTranslate } from '@ali/we-util';
import { getFontFamily } from "./getFontFamily";

/**
 * 获取fontFamily, 字体探测结果, 语言转换函数
 * @param locale 本地语言类型
 * @returns {[options, installedRecord, t]}
 */
export var useFontFamily = function useFontFamily(locale) {
  var t = useTranslate(locale); // 字体列表选项

  var options = useMemo(function () {
    var fontFamilies = getFontFamily(function (s) {
      return t(s);
    });
    return fontFamilies;
  }, [t]);
  return [options, t];
};