import type { IModalProps } from "../modal";
export interface IOptionsModalOption {
    color?: string;
    value: string;
    id?: string;
}
export interface IOptionsModalTheme {
    iconColors: string[];
    colors: string[];
}
export interface IOptionsModalOptionWithId extends IOptionsModalOption {
    id: string;
}
export interface IThemeProps {
    theme: IOptionsModalTheme;
}
export interface IOptionsModalProps extends Omit<IModalProps, 'onOk'> {
    /**
     * 主题列表
     */
    themes?: IOptionsModalTheme[];
    /**
     * 国际化
     */
    locale?: string;
    /**
     * 选项列表
     */
    options?: IOptionsModalOption[];
    /**
     * 默认选项列表
     */
    defaultOptions?: IOptionsModalOption[];
    /**
     * 移除按钮是否enable
     */
    removable?: boolean;
    /**
     * options变化函数
     */
    onChangeOptions?: (e: IOptionsModalOption[]) => void;
    /**
     * 是否需要颜色
     */
    needColor?: boolean;
    /**
     * 是否需要颜色变化
     */
    onChangeNeedColor?: (e: boolean) => void;
    /**
     * 点击清除列表
     */
    onClickRemove?: () => void;
    /**
     * 点击确定并通过个数判断
     */
    onOk?: (e: IOptionsModalOption[]) => void;
    /**
     * 层级
     */
    zIndex?: number;
    /**
     * 测试使用
     */
    testid?: string;
    /**
     * 提示
     */
    tipText?: string;
}
