import { useContext } from 'react';
import { FileMenuContext } from "./context";
/**
 * 获取 Controller
 *
 * @param {String} name Component's name
 * @return {Controller | never}
 */

export var useController = function useController(name) {
  var ctrl = useContext(FileMenuContext);

  if (!ctrl) {
    throw new Error(name + " \u7EC4\u4EF6\u5FC5\u987B\u4E3A FileMenu \u7EC4\u4EF6\u7684\u5B50\u7EC4\u4EF6");
  }

  return ctrl;
};